import{_ as a,X as e,Y as i,a2 as r}from"./framework-c2b0d87a.js";const t="/assets/2e-小鹤双拼hejp-fc48c8c4.png",n="/assets/记忆口诀-fa7aaae0.png",s={},o=r('<h2 id="一、双拼方案" tabindex="-1"><a class="header-anchor" href="#一、双拼方案" aria-hidden="true">#</a> 一、双拼方案：</h2><p>使用两个字母对汉字进行编码的方案，第一个字母表示声母，第二字母表示韵母，没有声母时用零声母代替<br> 单字母声母韵母键位不变，双字母声母和多字母韵母位置见“双拼键位图”</p><h2 id="二、零声母方案" tabindex="-1"><a class="header-anchor" href="#二、零声母方案" aria-hidden="true">#</a> 二、零声母方案：</h2><p>指全拼中无声母的韵母音节在双拼中的处理方案，用来充当其声母的字母即零声母</p><ol><li>本方案以韵母首字母为零声母，即把韵母的首字母当作声母<br> 单字母韵母，零声母 + 韵母所在键，如： 啊＝aa 哦=oo 额=ee<br> 双字母韵母，零声母 + 韵母末字母，如： 爱＝ai 恩=en 欧=ou<br> 三字母韵母，零声母 + 韵母所在键，如： 昂＝ah</li><li>简单说：<strong>双字母音节保持全拼方式，一三字母音节为首字母加韵母所在键</strong></li></ol><h2 id="三、双拼键位图" tabindex="-1"><a class="header-anchor" href="#三、双拼键位图" aria-hidden="true">#</a> 三、双拼键位图</h2><figure><img src="'+t+'" alt="小鹤双拼hejp" tabindex="0" loading="lazy"><figcaption>小鹤双拼hejp</figcaption></figure><ul><li>学习过程：一小时记忆键位，一周习惯双拼节奏，一月恢复全拼时速度</li><li>请在各拼音输入法的设置中选择本方案使用</li></ul><hr><p>记忆口诀 <img src="'+n+'" alt="记忆口诀" loading="lazy"></p>',10),c=[o];function l(h,_){return e(),i("div",null,c)}const p=a(s,[["render",l],["__file","2e-小鹤双拼.html.vue"]]);export{p as default};
